/* eslint-disable no-unused-vars */
/* eslint-disable react/prop-types */
/* eslint-disable react/jsx-props-no-spreading */
import React from 'react'
import { Box, Heading, Flex, Text, Button, Link } from '@chakra-ui/core'
import { FiGithub } from "react-icons/fi";
import { DiNpm } from "react-icons/di";
import { Container } from 'react-grid-system';

const MenuItems = ({ children }) => (
  <Text mt={{ base: 4, md: 0 }} mr={6} display="block">
    {children}
  </Text>
)

const Header = (props) => {
  const [show, setShow] = React.useState(false)

  return (
    <Flex
      as="nav"
      align="center"
      justify="space-between"
      wrap="wrap"
      padding="1rem"
      color="#5bc0be"
      style={{  boxShadow: '0 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24)'}}
      {...props}
    >
      <Flex align="center" mr={5}>
        <Heading as="h1" size="lg" letterSpacing='-.1rem' style={{fontWeight:'300', fontSize:40}}>
          React Data Form
        </Heading>
      </Flex>

      <Box
        display={{ sm: show ? 'block' : 'none', md: 'flex' }}
        width={{ sm: 'full', md: 'auto' }}
        alignItems="center"
        flexGrow={1}
      >
        <MenuItems><Link href="/">Get Started</Link></MenuItems>
        <MenuItems><Link href="/examples">Examples</Link></MenuItems>
      </Box>

      <Box as="a" mr="10" href="https://github.com/Jucian0/useform" target="_blank">
        <FiGithub size="2em" />
      </Box>
      <Box as="a" mr="10" href="https://www.npmjs.com/package/useforms" target="_blank">
        <DiNpm size="2em" />
      </Box>
    </Flex>
  )
}

export default Header
